import React, { Component } from 'react';
import styled from 'styled-components';
import {ProductConsumer} from '../content';
import {Link} from 'react-router-dom';
export default class Product extends Component{
render(){

    const {id,title,img,price,inCart} = this.props.value;

    return(

        <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3">
      <div className="card">
        <div className="img-container p-5" onClick={()=>console.log("from click me")}>
            <Link to="/details">
                <img src ={img} alt="product" className="card-img-top" />
            </Link>
            <button className="cart-btn" disabled={inCart?true:false} 
            onClick={
                ()=>{
                    console.log("added to the cart");
                }
            }>
                {inCart? (<p className="text-capitalize mb-0" disabled> inCart</p>):
              <i class="material-icons">
              add_shopping_cart
              </i> }
            </button>
            {/*Card Footer */}

            <div className="card-footer d-flex justify-context-between">
                <p className="align-self-center mb-0">
                    {title}
                </p>
                <h5 className="text-blue font-italic mb-0">
                    <span className="mr-1">
                        {price}
                    </span>
                </h5>
            </div>
        </div>
      </div>
        </ProductWrapper >
    );
}

}


const ProductWrapper = styled.div ``;
